function parser()
	while true do wait(60000)
		for nick, info in pairs(jchat) do 
			if #info > 0 then
				for i = #info, 1, -1 do
					if os.time() - info[i][3] >= (set.maxlog[0] * 60) then
						table.remove(jchat[nick], i)
					end
				end
			end
		end
	end
end

function strLower(s)
    for i = 192, 223 do
        s = s:gsub(_G.string.char(i), _G.string.char(i + 32))
    end
    s = s:gsub(_G.string.char(168), _G.string.char(184))
    return s:lower()
end

function strUpper(s)
    for i = 224, 255 do
        s = s:gsub(_G.string.char(i), _G.string.char(i - 32))
    end
    s = s:gsub(_G.string.char(184), _G.string.char(168))
    return s:upper()
end

function markTextByPart(search, str, color)
    local tohex = function(argb)
        local color = ('%x'):format(bit.band(argb, 0xFFFFFF))
        return ('{%s%s}'):format(('0'):rep(6 - #color), color)
    end
    local color = color and tohex(color) or tohex(0xFFFF0000)
    for l in search:gmatch('.') do
        l = l:gsub('[%(%)%.%%%+%-%*%?%[%]%^%$]', '%%%1')
        search = search:gsub(l, ('[%s%s]'):format(strLower(l), strUpper(l)))
    end
    return str:gsub(search, color..'%1{FFFFFF}')
end

