function se.onServerMessage(col, msg)
	local tag, player, id, text = msg:match('%[(.+)%] (.-)%[(%d+)]: (.+)')
	if tag == 'Таксист' or tag == 'Дальнобойщик' or tag == 'Механик' or tag == 'Пилот' or tag == 'Машинист электропоезда' or tag == 'Адвокат' or tag == 'Пожарный' or tag == 'Водитель трамвая' or tag == 'Главный фермер' or tag == 'Водитель автобуса' or tag == 'Работник Налоговой' or tag == 'Развозчик продуктов' or tag == 'Мусорщик' or tag == 'Металлоломщик' or tag == 'Крупье' or tag == 'Инкассатор' or tag == 'Ремонтник дорог' or tag == 'Руководитель грузчиков' or tag == 'Руководитель завода' then
        local matExists
		if not jchat[player] then jchat[player] = {} end
        if muted[player] then muted[player] = nil end
		local isWarn, time, count, similarity = check(player, text)
        matExists, msg_h_mat = checkMat(msg)

		table.insert(jchat[player], { msg, text, os.time(), {isWarn, time, count, similarity} })
		table.insert(last, { msg, text, os.time(), {isWarn, time, count, similarity}, player })
    
        if isWarn or matExists then
            table.insert(warn_pool, { msg, text, os.time(), {isWarn, time, count, similarity}, player })
            while #warn_pool > set.pagesize[0] do table.remove(warn_pool, 1) end
        end

		while #last > set.pagesize[0] do table.remove(last, 1) end
		if set.render[0] == 2 then
			if show.mat[0] then 
				msg = msg_h_mat
			end	

			if only_warn[0] and not isWarn and not matExists then
				return false
			end

			return {col, msg}
		end
		writeLog(msg)
		return false
	end

    local nick = string.match(msg, "^ Администратор [A-z0-9_]+%[%d+%] заглушил игрока ([A-z0-9_]+)%[%d+%] на %d+ минут")
    if nick ~= nil and jchat[nick] then muted[nick] = true end

    local nick = string.match(msg, "^ Администратор [A-z0-9_]+ установил в оффлайне %d+ минут молчанки игроку ([A-z0-9_]+)")
    if nick ~= nil and jchat[nick] then muted[nick] = true end
end

function se.onTogglePlayerSpectating(status)
	recon = status
	if settings[0] and recon then settings[0] = false end
	if recon and set.render[0] == 1 and not show.inrecon[0] then
		set.render[0] = 2
		lua_thread.create(function()
			while recon do wait(0) end
			set.render[0] = 1
		end)
	end
end

function check(player, text)
	local at = os.time()
	if game_focused or getGameDirectory():find("ARIZONA GAMES") then
		for nick, info in pairs(jchat) do 
			if #info > 0 and nick == player then
				for i = #info, 1, -1 do
					local similarity = function(text_old, text_new)
						local lev = string.levenshtein(text_old, text_new)
						return 100 * (1 - lev / math.max(#text_old, #text_new))
					end
					local sim = similarity(info[i][2], text)
					if sim >= set.minSim[0] and (at - info[i][3]) < (set.maxlog[0] * 60) then
						return true, info[i][3], info[i][4][3] + 1, sim
					end
				end
			end
		end
	end
	return false, at, 1, 0
end

function checkMat(text)
	local result = false
	local t = {}
	for s in text:gmatch('[^%s]+') do
		table.insert(t, s)
	end
	for i, v in ipairs(t) do
		if mat[strLower(v:gsub('[^А-я]+', ''))] ~= nil then
			t[i] = v:gsub(v, ABGRtoStringRGB(cfg.main.colMat) .. v .. '{FFFFFF}')
			result = true
		end
	end
	return result, table.concat(t, ' ')
end

function ABGRtoStringRGB(abgr)
	local a, b, g, r = explode_U32(abgr)
	local argb = join_argb(a, r, g, b)
	local color = ('%x'):format(bit.band(argb, 0xFFFFFF))
	return ('{%s%s}'):format(('0'):rep(6 - #color), color)
end

function string.levenshtein(string1, string2)
    local string1_len = string.len(string1)
    local string2_len = string.len(string2)
    local matrix = {}
    local cost = 0
 
    if (string1_len == 0) then
        return string2_len
    elseif (string2_len == 0) then
        return string1_len
    elseif (string1 == string2) then
        return 0
    end
 
    for i = 0, string1_len, 1 do
        matrix[i] = {}
        matrix[i][0] = i
    end
    for j = 0, string2_len, 1 do
        matrix[0][j] = j
    end
 
    for i = 1, string1_len, 1 do
        for j = 1, string2_len, 1 do
            if (string1:byte(i) == string2:byte(j)) then
                cost = 0
            else
                cost = 1
            end
         
            matrix[i][j] = math.min(matrix[i-1][j] + 1, matrix[i][j-1] + 1, matrix[i-1][j-1] + cost)
        end
    end
 	
    return matrix[string1_len][string2_len]
end

function renderFontDrawTextAlign(font, text, x, y, color, align)
	align = align or 1
    if align == 1 then renderFontDrawText(font, text, x, y, color) end
    if align == 2 then renderFontDrawText(font, text, x - renderGetFontDrawTextLength(font, text) / 2, y, color) end
    if align == 3 then renderFontDrawText(font, text, x - renderGetFontDrawTextLength(font, text), y, color) end
end

function renderFontDrawClickableText(active, font, text, posX, posY, color, color_hovered, align, b_symbol)
	local cursorX, cursorY = getCursorPos()
	local lenght = renderGetFontDrawTextLength(font, text)
	local height = renderGetFontDrawHeight(font)
	local symb_len = renderGetFontDrawTextLength(font, '>')
	local hovered = false
	local result = false
    b_symbol = b_symbol == nil and false or b_symbol
    align = align or 1

    if align == 2 then
    	posX = posX - (lenght / 2)
    elseif align == 3 then
    	posX = posX - lenght
	end

    if active and cursorX > posX and cursorY > posY and cursorX < posX + lenght and cursorY < posY + height then
        hovered = true
        if isKeyJustPressed(0x01) then -- LButton
        	result = true 
        end
    end

    local anim = math.floor(math.sin(os.clock() * 10) * 3 + 5)

 	if hovered and b_symbol and (align == 2 or align == 1) then
    	renderFontDrawText(font, '>', posX - symb_len - anim, posY, 0x90FFFFFF)
    end 

    renderFontDrawText(font, text, posX, posY, hovered and color_hovered or color)

    if hovered and b_symbol and (align == 2 or align == 3) then
    	renderFontDrawText(font, '<', posX + lenght + anim, posY, 0x90FFFFFF)
    end

    return result
end

function clock_normal(time)
    local timezone_offset = 86400 - os.date('%H', 0) * 3600
    return time + timezone_offset
end

