function sampGetPlayerIdByNickname(nickname)
   	local result = nil
    for id = 0, 999 do
        if sampIsPlayerConnected(id) then
            if sampGetPlayerNickname(id) == tostring(nickname) then 
            	result = id
            	break
            end
        end
    end
    return result
end

