function imgui.CenterText(text, color)
	local win = imgui.GetWindowSize().x
	local len = imgui.CalcTextSize(text).x
	imgui.SetCursorPosX((win - len) / 2)
	if color ~= nil then
		imgui.TextColored(color, text)
		return 
	end
	imgui.Text(text)
end

function AlignedText(text, align, color)
	color = color or imgui.GetStyle().Colors[imgui.Col.Text]
	local width = imgui.GetWindowWidth()
	for line in text:gmatch('[^\n]+') do
		local lenght = imgui.CalcTextSize(line).x
		if align == 2 then
			imgui.SetCursorPosX((width - lenght) / 2)
		elseif align == 3 then
			imgui.SetCursorPosX(width - lenght - imgui.GetStyle().WindowPadding.x)
		end
		imgui.TextColored(color, line)
	end
end

